/**
 * API 接口类型定义模块
 *
 * 提供所有后端接口的类型定义
 *
 * ## 主要功能
 *
 * - 通用类型（分页参数、响应结构等）
 * - 认证类型（登录、用户信息等）
 * - 系统管理类型（用户、角色等）
 * - 全局命名空间声明
 *
 * ## 使用场景
 *
 * - API 请求参数类型约束
 * - API 响应数据类型定义
 * - 接口文档类型同步
 *
 * ## 注意事项
 *
 * - 在 .vue 文件使用需要在 eslint.config.mjs 中配置 globals: { Api: 'readonly' }
 * - 使用全局命名空间，无需导入即可使用
 *
 * ## 使用方式
 *
 * ```typescript
 * const params: Api.Auth.LoginParams = { userName: 'admin', password: '123456' }
 * const response: Api.Auth.UserInfo = await fetchUserInfo()
 * ```
 *
 * @module types/api/api

 */

declare namespace Api {
  /** 通用类型 */
  namespace Common {
    /** 分页参数 */
    interface PaginationParams {
      /** 当前页码 */
      current: number
      /** 每页条数 */
      size: number
      /** 总条数 */
      total: number
    }

    /** 通用搜索参数 */
    type CommonSearchParams = Pick<PaginationParams, 'current' | 'size'>

    /** 分页响应基础结构 */
    interface PaginatedResponse<T = any> {
      records: T[]
      current: number
      size: number
      total: number
    }

    /** 启用状态 */
    type EnableStatus = '1' | '2'

    /** 字典项 */
    interface DicItem {
      name: string
      code: string
    }

    interface OptionItem {
      label: string
      value: string
      disabled?: boolean
    }

    interface CascaderOptionsItem {
      value: string
      label: string
      children?: CascaderOptionsItem[]
    }

    interface TreeItem {
      id: string
      label: string
      children?: TreeItem[]
    }
  }

  /** 认证类型 */
  namespace Auth {
    /** 登录参数 */
    interface LoginParams {
      userName: string
      password: string
    }

    /** 登录响应 */
    interface LoginResponse {
      token: string
      refreshToken: string
    }

    /** 用户信息 */
    interface UserInfo {
      buttons: string[]
      roles: string[]
      userId: number
      userName: string
      email: string
      avatar?: string
    }
  }

  /** 系统管理类型 */
  namespace SystemManage {
    /** 用户列表 */
    type UserPage = Api.Common.PaginatedResponse<UserListItem>

    /** 用户列表项 */
    interface UserListItem {
      id: string
      account: string
      password: string
      username: string
      email: string
      gender: DicItem
      phone: string
      status: DicItem
      roles: string
      createBy: string
      createTime: string
      updateBy: string
      updateTime: string
    }

    /** 用户搜索参数 */
    type UserSearchParams = Partial<
      Pick<UserListItem, 'id' | 'userName' | 'userGender' | 'userPhone' | 'userEmail' | 'status'> &
        Api.Common.CommonSearchParams
    >

    interface UserSaveParams {
      account: string
      password: string
      username: string
      email: string
      gender: string
      phone: string
      status: string
      roles: string[]
    }

    /** 角色列表 */
    type RoleList = Api.Common.PaginatedResponse<RoleListItem>

    /** 角色列表项 */
    interface RoleListItem {
      id: string
      name: string
      code: string
      description: string
      isEnabled: number
      createTime: string
    }

    /** 角色搜索参数 */
    type RoleSearchParams = Partial<
      Pick<RoleListItem, 'id' | 'name' | 'code' | 'description' | 'isEnabled'> &
        Api.Common.CommonSearchParams
    >

    /** 角色菜单 */
    interface RoleMenu {
      roleId: string | undefined
      checkedKeys: string[]
      halfCheckedKeys: string[]
    }

    type DictList = Api.Common.PaginatedResponse<DictItem>

    interface DictDataItem {
      id: string
      type: string
      code: string
      label: string
      isEnabled: boolean
      sort: number
    }

    type DictDataSearchParams = Partial<
      Pick<DictItem, 'id' | 'type' | 'code' | 'label' | 'isEnabled'> & Api.Common.CommonSearchParams
    >

    type DictTypeList = Api.Common.PaginatedResponse<DictTypeItem>

    interface DictTypeItem {
      id: string
      type: string
      name: string
      module: string
      dictDataList?: DictDataItem[]
      description: string
      createTime: string
      code: string
      label: string
      isEnabled: boolean
      sort: number
    }

    interface MenuFormData {
      name: string
      path: string
      component?: string
      icon?: string
      roles?: string[]
      sort?: number
      [key: string]: any
    }

    interface DictMap {
      [key: string]: OptionItem[]
    }
  }
  /** 资源管理类型 */
  namespace ResourcesManage {
    /** 学校列表 */
    type SchoolPage = Api.Common.PaginatedResponse<SchoolListItem>
    /** 学校列表项 */
    interface SchoolItem {
      id: string
      schoolCode: string
      schoolName: string
      schoolAbbr: string
      englishName: string
      englishAbbr: string
      address: string
      intro: string
      createTime: string
    }
    /** 校区列表 */
    type CampusPage = Api.Common.PaginatedResponse<CampusItem>

    /** 校区列表项 */
    interface CampusItem {
      id: string
      campusCode: string
      schoolId: string
      campusName: string
      englishName: string
      address: string
      principal: string
      phone: string
      intro: string
      createTime: string
    }

    /** 校区搜索参数 */
    type CampusSearchParams = Partial<
      Pick<
        CampusItem,
        | 'id'
        | 'campusCode'
        | 'campusName'
        | 'englishName'
        | 'address'
        | 'principal'
        | 'phone'
        | 'intro'
      > &
        Api.Common.CommonSearchParams
    >

    /** 学院列表 */
    type CollegePage = Api.Common.PaginatedResponse<CollegeItem>

    /** 学院列表项 */
    interface CollegeItem {
      id: string
      collegeCode: string
      campusId: string
      collegeName: string
      collegeAbbr: string
      englishName: string
      dean: string
      contact: string
      intro: string
    }

    /** 学院搜索参数 */
    type CollegeSearchParams = Partial<
      Pick<CollegeItem, 'id' | 'collegeName' | 'englishName' | 'dean' | 'contact' | 'intro'> &
        Api.Common.CommonSearchParams
    >

    type UnitPage = Api.Common.PaginatedResponse<UnitItem>

    interface UnitItem {
      id: string
      unitCode: string
      unitName: string
      unitAbbr: string
      englishName: string
      englishAbbr: string
      parentId: string
      isResearchSection: boolean
      unitType: string
      unitCategory: string
      address: string
      partySecretary: string
      adminPrincipal: string
    }

    /** 单位搜索参数 */
    type UnitSearchParams = Partial<
      Pick<
        UnitItem,
        | 'id'
        | 'unitCode'
        | 'unitName'
        | 'unitAbbr'
        | 'englishName'
        | 'englishAbbr'
        | 'parentId'
        | 'isResearchSection'
        | 'unitType'
        | 'unitCategory'
        | 'address'
        | 'partySecretary'
        | 'adminPrincipal'
      > &
        Api.Common.CommonSearchParams
    >
  }
  namespace FileManage {
    /** 上传文件参数 */
    interface UploadFileParams {
      file: File
      directoryId: string
    }
    /** 上传文件响应 */
    interface UploadFileResponse {
      id: string
    }
  }
}
